"use client";

import { useState, useEffect } from "react";
import Image from "next/image";
import {
  Box,
  Typography,
  TextField,
  Button,
  Paper,
  Grid,
} from "@mui/material";

const CompanyDetailsPage = () => {
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    headerImage: "",
    favIcon: "",
  });
  const [companyDetails, setCompanyDetails] = useState(null);
  const [headerImageFile, setHeaderImageFile] = useState(null);
  const [favIconFile, setFavIconFile] = useState(null);
  const [headerImagePreview, setHeaderImagePreview] = useState(null);
  const [favIconPreview, setFavIconPreview] = useState(null);

  useEffect(() => {
    async function fetchCompanyDetails() {
      console.log("Fetching company details...");
      try {
        const response = await fetch("/api/companydetails");
        const data = await response.json();
        console.log("Fetched data:", data);
        if (data) {
          setCompanyDetails(data);
          setFormData({
            name: data.name || "",
            description: data.description || "",
            headerImage: data.headerImage || "",
            favIcon: data.favIcon || "",
          });
          setHeaderImagePreview(
            data.headerImage
              ? `${process.env.NEXT_PUBLIC_UPLOADED_IMAGE_URL}/${data.headerImage}`
              : null
          );
          setFavIconPreview(
            data.favIcon
              ? `${process.env.NEXT_PUBLIC_UPLOADED_IMAGE_URL}/${data.favIcon}`
              : null
          );
        }
      } catch (error) {
        console.error("Error fetching company details:", error);
      }
    }
    fetchCompanyDetails();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    console.log(`Updated ${name} to:`, value);
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    const file = files[0];
    if (name === "headerImage") {
      setHeaderImageFile(file);
      setHeaderImagePreview(URL.createObjectURL(file));
      console.log("Selected header image file:", file);
    } else if (name === "favIcon") {
      setFavIconFile(file);
      setFavIconPreview(URL.createObjectURL(file));
      console.log("Selected favicon file:", file);
    }
  };

  const convertToBase64 = (file) => {
    console.log("Converting file to Base64:", file);
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => {
        console.log("Base64 conversion successful");
        resolve(reader.result);
      };
      reader.onerror = (error) => {
        console.error("Base64 conversion failed:", error);
        reject(error);
      };
    });
  };

  const uploadImage = async (base64Image, type) => {
    console.log("Uploading image...");
    try {
      const response = await fetch(`${process.env.NEXT_PUBLIC_UPLOAD_IMAGE_API}`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ image: base64Image, type: type }),
      });
      const result = await response.json();
      console.log("Upload response received:", result);

      const uploadedUrl = result.url || result.image_url || null;
      console.log("Final uploaded URL:", uploadedUrl);

      return uploadedUrl;
    } catch (error) {
      console.error("Error uploading image:", error);
      return null;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Starting submission process...");

    let headerImageUrl = formData.headerImage;
    let favIconUrl = formData.favIcon;

    if (headerImageFile) {
      console.log("Uploading header image...");
      const headerImageBase64 = await convertToBase64(headerImageFile);
      const uploadedHeaderImageUrl = await uploadImage(headerImageBase64);
      if (uploadedHeaderImageUrl) headerImageUrl = uploadedHeaderImageUrl;
      console.log("Header image URL after upload:", headerImageUrl);
    }

    if (favIconFile) {
      console.log("Uploading favicon...");
      const favIconBase64 = await convertToBase64(favIconFile);
      const uploadedFavIconUrl = await uploadImage(favIconBase64, "ico");
      if (uploadedFavIconUrl) favIconUrl = uploadedFavIconUrl;
      console.log("Favicon URL after upload:", favIconUrl);
    }

    const updatedFormData = {
      ...formData,
      headerImage: headerImageUrl,
      favIcon: favIconUrl,
    };

    try {
      if (companyDetails && companyDetails.id) {
        console.log("Updating existing company details with ID:", companyDetails.id);
        await fetch(`/api/companydetails/${companyDetails.id}`, {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(updatedFormData),
        });
      } else {
        console.log("Creating new company details...");
        const response = await fetch("/api/companydetails", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(updatedFormData),
        });
        const data = await response.json();
        setCompanyDetails(data);
        console.log("New company details created:", data);
      }
      alert("Company details saved successfully!");
    } catch (error) {
      console.error("Error saving company details:", error);
      alert("Failed to save company details.");
    }
  };

  return (
    <Box sx={{ p: 3, maxWidth: 960, mx: "auto" }}>
      <Grid container spacing={4}>
        {/* Form Section on Left */}
        <Grid item xs={12} md={6}>
          <Paper elevation={3} sx={{ p: 3 }}>
            <Typography variant="h4" align="center" gutterBottom sx={{ color: "grey.800" }}>
              Company Details
            </Typography>
            <Box component="form" onSubmit={handleSubmit} sx={{ display: "flex", flexDirection: "column", gap: 2 }}>
              <TextField
                label="Name"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                fullWidth
                variant="outlined"
              />
              <TextField
                label="Description"
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                fullWidth
                variant="outlined"
                multiline
                rows={3}
              />
              <TextField
                label="Header Image"
                type="file"
                name="headerImage"
                onChange={handleFileChange}
                fullWidth
                variant="outlined"
                InputLabelProps={{ shrink: true }}
                inputProps={{ accept: "image/*" }}
              />
              <TextField
                label="Favicon"
                type="file"
                name="favIcon"
                onChange={handleFileChange}
                fullWidth
                variant="outlined"
                InputLabelProps={{ shrink: true }}
                inputProps={{ accept: "image/*" }}
              />
              <Button
                type="submit"
                variant="contained"
                color="primary"
                fullWidth
                sx={{ mt: 2, bgcolor: "grey.700", "&:hover": { bgcolor: "grey.800" } }}
              >
                {companyDetails ? "Update Company Details" : "Save Company Details"}
              </Button>
            </Box>
          </Paper>
        </Grid>

        {/* Image Preview Section on Right */}
        <Grid item xs={12} md={6}>
          <Box sx={{ display: "flex", flexDirection: "column", alignItems: "center", gap: 4 }}>
            {headerImagePreview && (
              <Box sx={{ textAlign: "center" }}>
                <Typography variant="h6" gutterBottom>
                  Header Image Preview
                </Typography>
                <Image
                  width={192} // Adjusted to match w-48 (48 * 4 = 192px)
                  height={192}
                  src={headerImagePreview}
                  alt="Header Preview"
                  placeholder="blur"
                  blurDataURL="data:image/jpeg;base64,"
                />
              </Box>
            )}
            {favIconPreview && (
              <Box sx={{ textAlign: "center" }}>
                <Typography variant="h6" gutterBottom>
                  Favicon Preview
                </Typography>
                <Image
                  width={64} // Adjusted to match w-16 (16 * 4 = 64px)
                  height={64}
                  src={favIconPreview}
                  alt="Favicon Preview"
                  placeholder="blur"
                  blurDataURL="data:image/jpeg;base64,"
                />
              </Box>
            )}
          </Box>
        </Grid>
      </Grid>
    </Box>
  );
};

export default CompanyDetailsPage;